export function dfs(grid, start, finish) {
  const [numRows, numCols] = [grid.length, grid[0].length];
  const visited = new Set();
  const path = [];

  function dfsAux(curr) {
    if (
      !curr ||
      curr.row >= numRows ||
      curr.col >= numCols ||
      curr.row < 0 ||
      curr.col < 0 ||
      visited.has(curr) ||
      curr.isWall
    ) {
      return;
    }
    console.log("visitando", curr);
    visited.add(curr);
    path.push(curr);
    const [i, j] = [curr.row, curr.col];
    if (curr != finish) {
      if (grid[i][j + 1]) {
        dfsAux(grid[i][j + 1]);
