import React from "react";
import "./Help.css";
import wall from "./img/wall.gif";
import select from "./img/select.gif";
import move from "./img/move.gif";

const Help = ({ open, setOpenHelp }) => {
  if (!open) return null;
  return (
    <div className="overlay" onClick={() => setOpenHelp(!open)}>
      <div className="modal-help" onClick={(e) => e.stopPropagation()}>
        <div>
          <p>
            Clique e arrasta para adicionar paredes. Para removê-las, clique em{" "}
            <span>Clear Walls</span>
          </p>
        </div>

        <p>Drag and drop para mover o node de start e de finish de lugar.</p>

        <p>
          Escolha entre os algoritmos disponíveis e clique em <span>RUN</span>{" "}
          para ver sua execução. Para executar novamente, clique em{" "}
          <span>reset grid.</span>
        </p>

        {/* <div className="text">

        </div>
        <div className="images">
          <img className="gif" src={wall}></img>
          <img className="gif" src={move}></img>
          <img className="gif" src={select}></img>
        </div>
        <button className="ok-btn" onClick={() => setOpenHelp(!open)}>
          OK
        </button> */}
      </div>
    </div>
  );
};

export default Help;
