import React from "react";
import Node from "./Node";
import "./Grid.css";
import Menu from "./Menu";
import { dfs } from "./algorithms/dfs";
import { bfs } from "./algorithms/bfs";
const Grid = () => {
  const [gridSize, setGridSize] = React.useState([5, 5]);
  const [startCoordinates, setStartCoordinates] = React.useState([4, 0]);
  const [finishCoordinates, setFinishCoordinates] = React.useState([0, 4]);
  const [startNode, setStartNode] = React.useState(null);
  const [finishNode, setFinishNode] = React.useState(null);

  const [isDrawing, setIsDrawing] = React.useState(false);
  const [isMoving, setIsMoving] = React.useState(false);
  const [movingNode, setMovingNode] = React.useState(null);

  const [grid, setGrid] = React.useState([]);

  function nodeConstructor(row, col) {
    const node = {
      row,
      col,
      isStart: row === startCoordinates[0] && col === startCoordinates[1],
      isFinish: row === finishCoordinates[0] && col === finishCoordinates[1],
      isWall: false,
      isVisited: false,
    };
    return node;
  }

  function gridConstructor() {
    const initialGrid = [];
    const sizeRows = gridSize[0];
    const sizeCols = gridSize[1];

    for (let i = 0; i <= sizeRows; i++) {
      const row = [];
      for (let j = 0; j <= sizeCols; j++) {
        row.push(nodeConstructor(i, j));
      }
      initialGrid.push(row);
    }
    return initialGrid;
  }

  function startDrawing(event, row, col) {
    event.preventDefault();
    if (!grid[row][col].isStart && !grid[row][col].isFinish) {
      setIsDrawing(true);
    } else {
      console.log(event.target.parentNode.parentNode);
      if (grid[row][col].isStart) {
        grid[row][col].isStart = false;
        setMovingNode("start");
      } else {
        grid[row][col].isFinish = false;
        setMovingNode("finish");
      }
      setIsMoving(true);
    }
  }

  function finishDrawing(event, row, col) {
    event.preventDefault();
    if (isDrawing) {
      setIsDrawing(false);
    }
    if (isMoving) {
      const newGrid = grid.slice();
      if (movingNode === "start") {
        newGrid[row][col].isStart = true;
        setStartCoordinates([row, col]);
      } else if (movingNode === "finish") {
        newGrid[row][col].isFinish = true;
        setFinishCoordinates([row, col]);
      }
      setGrid(newGrid);
      setMovingNode(null);
      setIsMoving(false);
    }
  }

  function draw({ target }, row, col) {
    if (isDrawing && !grid[row][col].isStart && !grid[row][col].isFinish) {
      const newGrid = grid.slice();
      newGrid[row][col].isWall = true;
      setGrid(newGrid);
    }
  }

  function clearGrid() {
    const newGrid = grid.slice();

    newGrid.map((row) =>
      row.map((node) => {
        node.isWall = false;
      })
    );
    setGrid(newGrid);
  }

  function runAlgorithm(algorithm) {
    switch (algorithm) {
      case "dfs":
        let path = dfs(
          grid,
          grid[startCoordinates[0]][startCoordinates[1]],
          grid[finishCoordinates[0]][finishCoordinates[1]]
        );
        break;
      case "dijkstra":
        break;
      case "bfs":
        break;
      default:
        break;
    }
  }

  React.useEffect(() => {
    const newGrid = gridConstructor();
    setGrid(newGrid);
  }, []);

  return (
    <>
      <Menu clearGrid={clearGrid} runAlgorithm={runAlgorithm} />
      <button
        onClick={() => {
          const path = dfs(
            grid,
            grid[startCoordinates[0]][startCoordinates[1]],
            grid[finishCoordinates[0]][finishCoordinates[1]]
          );

          console.log("path", path);
          const newGrid = grid.slice();
          setGrid(newGrid);
        }}
      >
        RUN
      </button>
      <table className={isMoving ? "grid grid-on-moving" : "grid"}>
        <tbody>
          {grid.map((row, i) => (
            <tr key={i}>
              {row.map((node) => (
                <Node
                  {...node}
                  key={node.col}
                  onMouseDown={(event) =>
                    startDrawing(event, node.row, node.col)
                  }
                  onMouseUp={(event) =>
                    finishDrawing(event, node.row, node.col)
                  }
                  onMouseEnter={(event) => draw(event, node.row, node.col)}
                />
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Grid;
