    ? "start"
    : isFinish
    ? "finish"
    : isWall
    ? "wall"
    : "";

  return (
    <td
      className={`node ${additionalClass}`}
      onMouseDown={isPressed}
      onMouseUp={onMouseUp}
      onMouseEnter={onMouseEnter}
    ></td>
  );
};

export default Node;
